s->dashboard_model->Getocc_status();				return options($status, 'status_id', '', 'status_desc');	}	public function getocc_unit() {		$unit = $this->dashboard_model->Getocc_unit();				return options($unit, 'unit', '', 'unit');	}	public function getocc_acreg_ga() {		$unit = $this->dashboard_model->Getocc_accreg();				return options($unit, 'acreg', '', 'acreg');	}	public function mobile_detect(){		$this->load->library('user_agent');			if ($this->agent->is_browser()){			    $agent = $this->agent->browser().' '.$this->agent->version();			}			elseif ($this->agent->is_robot()){			    $agent = $this->agent->robot();			}			elseif ($this->agent->is_mobile()){			    $agent = $this->agent->mobile();			}			else{			    $agent = 'Unidentified User Agent';			}			echo $agent;			echo $this->agent->platform(); // Platform info (Windows, Linux, Mac, etc.)	}}