(){    $('.dz-preview ').remove();    $('.dz-filename').remove();    $('#file_handler').modal({backdrop: 'static', keyboard: false},'show');});function check(){    $('#file_handler').modal('hide');}function remove(id){    $('#bb'+id+'').remove();    // $('#f1'+id+'').remove();    // $('#lb'+id+'').remove();}$(document).ready(function() {    var counter = 0;    Dropzone.autoDiscover = false;        var fileList = new Array;        var i =0;        $("#hallodz").dropzone({            addRemoveLinks: true,            init: function() {                this.on("success", function(file, serverFileName) {                    fileList[i] = {"serverFileName" : serverFileName,"fileName" : file.name,"fileId" : i };                    // console.log(fileList);                        var a = fileList[i].serverFileName;                        var a = a.replace(/\s/g,'');                        var fn =a.substring(0, 30);                        var recep = '<input type="hidden" id="f1'+(i)+'" class="nm_file" name="file_name[]" value="'+a+'"><i class="fileclear" id="lb'+(i)+'">'+fn+'... <a id="bb'+(counter)+'" href="#" onclick="remove('+(i)+')" >remove</a><br></i>';                        $('#filegetname').append(recep);                        i++;                    });            },            url: "<?=site_url('site/attach_ftp_guest')?>"    });});</script>